'''This package sets up the admin interface for the :mod:`communication` app.'''from django.contrib import adminfrom communication.models import LabAddress, LabLocation, Postclass LabAddressAdmin(admin.ModelAdmin):    '''The :class:`~communication.models.LabAddress` model admin is the default.'''    passadmin.site.register(LabAddress, LabAddressAdmin)class LabLocationAdmin(admin.ModelAdmin):    '''The :class:`~communication.models.LabLocation` model admin is the default.'''    passadmin.site.register(LabLocation, LabLocationAdmin)class PostAdmin(admin.ModelAdmin):    '''The :class:`~communication.models.Post` model admin is the default.'''    passadmin.site.register(Post, PostAdmin)